import styled from "styled-components";
import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons';


const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  background-color:orange;
`;

const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%; //弄成圓圈
  display: flex;
  align-items: center;//讓他們對中間
  justify-content: center;
  position: absolute; //什麼位置都可以放，會無視次序
  top: 0;
  bottom: 0;
  left: ${props=> props.direction === "left" && "10px"}; //left and 10pixel
  right: ${props=> props.direction === "right" && "10px"}; //right and 10 pixel
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`;

const Wrapper = styled.div`
  height: 100%;
`;

const Slide = styled.div`
  display:flex;
  align-items: center;
`;

const ImgContainer = styled.div` // put image place
  flex: 1;
`;

const InfoContainer = styled.div` // put info page
  flex: 1;  
`;

const Slider = () => {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined/>
      </Arrow>
      <Wrapper>

      </Wrapper>
      <Arrow direction="right">
        <ArrowRightOutlined/>
      </Arrow>
    </Container>
  )
}

export default Slider
