import styled from "styled-components";
import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons';

//container store data
const Container = styled.div`
  width: 100%;
  height: 100vh; //100% of the view point view height
  display: flex;
  //background-color:orange;
`;

//Arrow style
const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%; //弄成圓圈
  display: flex;
  align-items: center;//讓他們對中間
  justify-content: center;
  position: absolute; //什麼位置都可以放，會無視次序
  top: 0;
  bottom: 0;
  left: ${props=> props.direction === "left" && "10px"}; //left and 10pixel
  right: ${props=> props.direction === "right" && "10px"}; //right and 10 pixel
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`;

//Wrapper like container but inside a container
const Wrapper = styled.div`
  height: 100%;
`;

//slide
const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display:flex;
  align-items: center;
`;

//image container
const ImgContainer = styled.div` // put image place
  height: 100%; //會換放照片的size
  flex: 1; //left
`;

//image file pattern
const Image = styled.img`
  height: 80%;
`;

//info container
const InfoContainer = styled.div` // put info page
  flex: 1;  //right
  padding: 200px;
  margin-left: -50px;
`;

//info Title
const Title = styled.h1`
  font-size: 70px;
`;

//info Title
const Subtitle = styled.h2`
  font-size: 50px;
`;

//info Description
const Desc = styled.p`
  margin: 50px 0px; //top and bottom 50 px left and right 0px是外面的
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
  text-align: justify;
`;

//Info button
const Button = styled.button`
  padding: 10px; //padding 是裡面的空間
  font-size: 20px;
  background-color: transparent;
  cursor: pointer;
`;


const Slider = () => {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined/>
      </Arrow>
      <Wrapper>
        <Slide>
          <ImgContainer>
            <Image src={require('/Users/szexian/Desktop/MUM/e-commerce/src/image/IMG_3376.jpg')}/> {/*path*/}
            {/*<Image src="https://i.ibb.co/XsdmR2c/1.png"/> {/*path*/}
          </ImgContainer>
          <InfoContainer>
            <Title>PINEAPPLE POP TARTS</Title>
            <Subtitle>SGD 25</Subtitle>
            <Desc>
              <p>Filled with homemade tangy sweet guilt free pineapple jam</p>
              <p>Smooth buttery crust baked to golden crisp perfection</p>
              Pop into the mouth one at a time and see it melts in your mouth
              Perfect gift for CNY to Ong your loved ones!
            </Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right">
        <ArrowRightOutlined/>
      </Arrow>
    </Container>
  )
}

export default Slider
