import React from 'react'
import styled from 'styled-components'
import { Search } from '@material-ui/icons'; //install at npm and import from material-ui

/* new design for div container*/
/*same like css*/
const Container = styled.div` 
  height: 60px;
`;
/* new design for div wrapper*/
/* have space between contain*/
const Wrapper = styled.div` 
  padding:10px 20px;
  display: flex; //flex will let all the contents show in horizontal
  justify-content: space-between; // 平均分布，然後空間剛剛好
  align-items: center;


`;
/*left*/
const Left = styled.div`
  flex:1; //1 for 1 box size better than width
  display: flex;
  align-items: center;

`;

// Language div
const Language = styled.span`
  font-size: 14px;
  cursor : pointer; //cursor will change to hand become clickable
`;

//Search Container
const SearchContainer = styled.div`
  border: 1px solid lightgray;
  display: flex;
  align-items: center;
  margin-left:25px; //左邊的空間
  padding: 5px; //component 裡面的空間

`;

//input
const Input =styled.input`

`;

//Center
const Center = styled.div`
  flex:1;
`;

//Right
const Right = styled.div`
  flex:1;    
`;

//write here to arrange the order
const Navbar = () => {
  return (
    <Container> 
      <Wrapper>
        <Left>
          <Language>EN</Language>
          <SearchContainer>
            <Input/> {/*div以外的都只是要寫關頭就可以了 */}
            <Search/> {/*Search Button*/}
          </SearchContainer>
        </Left>
        <Center>center</Center>
        <Right>right</Right>
      </Wrapper>
    </Container>
  )
}

export default Navbar
