import styled from "styled-components";
import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons';
import { useState } from "react";

//container store data
const Container = styled.div`
  width: 100%;
  height: 100vh; //100% of the view point view height
  display: flex; //all become horizontal
  position: relative; //relative to its normal position(normal place) x4 for the slide not resize
  overflow: hidden //overflow will not showing up
  //background-color:orange;
`;

//Arrow style
const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%; //弄成圓圈
  display: flex;
  align-items: center;//讓他們對中間
  justify-content: center;
  position: absolute; //什麼位置都可以放，會無視次序
  top: 0;
  bottom: 0;
  left: ${props=> props.direction === "left" && "10px"}; //props is direction left and 10pixel
  right: ${props=> props.direction === "right" && "10px"}; //right and 10 pixel
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
  z-index: 2; // place in front
`;

//Wrapper like container but inside a container for slider use
const Wrapper = styled.div`
  height: 100%;
  display: flex; //all become horizontal for the slide
  transform: translateX(0vw); // move on X-axis beginning is 0
`;

//slide
const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display:flex;
  align-items: center;
  background-color: ${props=>props.bg}; //follow slide props bg
`;

//image container
const ImgContainer = styled.div` // put image place
  height: 100%; //會換放照片的size
  flex: 1; //left
`;

//image file pattern
const Image = styled.img`
  height: 80%;
`;

//info container
const InfoContainer = styled.div` // put info page
  flex: 1;  //right
  padding: 200px;
  margin-left: -50px;
`;

//info Title
const Title = styled.h1`
  font-size: 70px;
`;

//info Title
const Subtitle = styled.h2`
  font-size: 50px;
`;

//info Description
const Desc = styled.p`
  margin: 50px 0px; //top and bottom 50 px left and right 0px是外面的
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
  text-align: justify;
`;

//Info button
const Button = styled.button`
  padding: 10px; //padding是裡面的空間
  font-size: 20px;
  background-color: transparent;
  cursor: pointer;
`;


const Slider = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const handleClick = (direction) => { /*function result back to direction */

  };

  return (
    <Container>
      <Arrow direction="left" onClick={()=>handleClick("left")}> {/*left and direction and call out arrow function*/}
        <ArrowLeftOutlined/>
      </Arrow>
      <Wrapper>
        {/*Slide 1*/}
        <Slide bg="f5fafd">
          <ImgContainer>
            <Image src={require('/Users/szexian/Desktop/MUM/e-commerce/src/image/IMG_3376.jpg')}/> {/*path*/}
            {/*<Image src="https://i.ibb.co/XsdmR2c/1.png"/> {/*path*/}
          </ImgContainer>
          <InfoContainer>
            <Title>PINEAPPLE POP TARTS</Title>
            <Subtitle>SGD 25</Subtitle>
            <Desc>
              Filled with homemade tangy sweet guilt free pineapple jam
              Smooth buttery crust baked to golden crisp perfection
              Pop into the mouth one at a time and see it melts in your mouth
              Perfect gift for CNY to Ong your loved ones!
            </Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
        {/*Slide 2*/}
        <Slide bg="fcf1ed">
          <ImgContainer>
            <Image src={require('/Users/szexian/Desktop/MUM/e-commerce/src/image/IMG_3385.jpg')}/>
          </ImgContainer>
          <InfoContainer>
            <Title>NUTTY CHOCOLATE COOKIES</Title>
            <Subtitle>SGD 23</Subtitle>
            <Desc>
              Infused with hints of rich dark chocolate
              Loaded with tasty and crunchy macadamia nuts
              The more you munch the more fragrant it gets and scrumptious to a fault!
            </Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
        {/*Slide 3*/}
        <Slide bg="f5fafd">
          <ImgContainer>
            <Image src={require('/Users/szexian/Desktop/MUM/e-commerce/src/image/IMG_3401.jpg')}/>
          </ImgContainer>
          <InfoContainer>
            <Title>SNOW FLAKES CRANBERRY COOKIES</Title>
            <Subtitle>SGD 18</Subtitle>
            <Desc>
            Rich creamy cranberry flavour
            Don't worry about paiseh food trap because they are wonderfully light and crisp
            Sweet but not jelak, a festive treat that no one can resists!
            </Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
        {/*Slide 4*/}
        <Slide bg="fbf0f4">
          <ImgContainer>
            <Image src={require('/Users/szexian/Desktop/MUM/e-commerce/src/image/IMG_3414.jpg')}/>
          </ImgContainer>
          <InfoContainer>
            <Title>WHITE ALMOND TILES</Title>
            <Subtitle>SGD 18</Subtitle>
            <Desc>
              Filled with homemade tangy sweet guilt free pineapple jam
              Smooth buttery crust baked to golden crisp perfection
              Pop into the mouth one at a time and see it melts in your mouth
              Perfect gift for CNY to Ong your loved ones!
            </Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right" onClick={()=>handleClick("right")}> 
        <ArrowRightOutlined/>
      </Arrow>
    </Container>
  )
}

export default Slider
