import { Facebook, Instagram, MailOutline, Phone, WhatsApp } from '@material-ui/icons';
import React from 'react'
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Logo = styled.h1`
  font-weight: bold;
  color: #A35127 ;
  letter-spacing: 10px;
`;

const Desc = styled.p`
  margin: 20px 0px; /*top and bottom 20px*/

`;

const SocialContainer = styled.div`
  display: flex;
`;

const SocialIcon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  color: white;
  background-color: #${props => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 20px;

`;

const Center = styled.div`
  flex: 1; 
  padding: 20px;
`;

const Title = styled.h3`
  margin-bottom: 30px;
`;

const List = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
  flex-wrap: wrap;
`;

const ListItem = styled.li`
  width: 50%;
  margin-bottom: 10px;
`;

const ContactItem = styled.div`
  margin-bottom: 20px;
  display: flex;
  align-content: center;
`;

const Payment = styled.img`

`;

const Right = styled.div`
  flex: 1;
  padding: 20px; 
`;


const Footer = () => {
  return (
    <Container>
      <Left>
        <Logo>AHMA'S HOMEMADE</Logo>
        <Desc>👵🏼Secret Recipe by two passionate Ah Ma👵<p>🏞 Every product of us has its own story 🍪!</p></Desc>
      </Left>
      <Center>
        <Title>Useful Links</Title>
        <List>
          <ListItem>Home</ListItem>
          <ListItem>Cart</ListItem>
          <ListItem>My Account</ListItem>
          <ListItem>Terms</ListItem>
        </List>
        <Desc>Copyright by AhMa's Homemade 2021</Desc>
      </Center>
      <Right>
        <Title>Contact Us</Title>
        <ContactItem>
          <Phone/>+65 9108 9652
        </ContactItem>
        <ContactItem>
          <MailOutline/>ahmahomemade0414@gmail.com
        </ContactItem>
      </Right>
    </Container>
  )
}

export default Footer
